sysauto_script.condition:
//Create the answer variable
var answer = true;

//Get today's date and determine which day of the week it is
var rightNow = new GlideDateTime();
var dayOfWeek = rightNow.getDayOfWeekLocalTime();

//if today is Saturday (6) or (7), set the answer variable to false
//so the Run this script field's script does not execute.
//Monday = 1, Tuesday = 2, etc.
if(dayOfWeek == 6 || dayOfWeek == 7){
	answer = false;
}
// End script with something that resolves to true of false. Ignore the warning.
answer;

sysauto_script.script:
//Wuery the database for NeedIt Task with Due date field values older
//than the current timw. Only return NeedIt Task records that do not have 
//a State field value of Closed Complete.
var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
overdueNITasks.addQuery('due_date','<=', rightNow);
overdueNITasks.addQuery('state', '!=', 3);

overdueNITasks.query();
//Write a log message for each overdue NeedIt Task record
while(overdueNITasks.next()){
	gs.info("Overdue NeedIt Task record: "+ overdueNITasks.number);
	gs.eventQueue('x_58872_needit.overdueNeedItTask',overdueNITasks,overdueNITasks.number,gs.getUserName());
}
